import React, { Component } from "react";
import { Link } from "react-scroll";

import ProfilePhoto from "../assets/profilePhoto.png";
import CV from "../documents/CharlieLeopard_Resume.pdf";

class Home extends Component {
  render() {
    return (
      <div id="homeSection">
        <div id="profile">
          <div id="welcomeMessage">
            <h2>Hi, I'm Charlie...</h2>
            <p>
              Welcome to my website, here you can find out more about me and the
              projects that I am working on. I'm a CS graduate and a versatile
              designer/developer with a particular interest in UX and ubiquitous technology.
            </p>
          </div>

          <div id="homeImage">
            <img src={ProfilePhoto} alt="Photo of Charlie" />
          </div>
        </div>

        <div id="welcomeButtons">
          <a href={CV} id="cvButton">
            My CV
          </a>
          <Link
            exact
            to="/contact"
            spy={true}
            smooth={true}
            duration={500}
            activeClass="active"
          >
            <a href="#" id="contactButton">
              Contact Me
            </a>
          </Link>
        </div>
      </div>
    );
  }
}

export default Home;
